import React, { Component } from "react";
import { withStyles } from "@material-ui/core/styles";
import InputAdornment from '@mui/material/InputAdornment';
import CssBaseline from "@mui/material/CssBaseline";
import { Paper } from "@mui/material";
import { Avatar } from "@mui/material";
import { FormControl, Input, InputLabel, Button} from "@mui/material"  ;
import { Snackbar,SnackbarContent } from "@mui/material";
import { TextField } from "@mui/material";

const Register =()=> {
 
const [user, setUser]=useState({
    email: "",
    password: "",
    passwordConfrim: "",
  })
  
  const Mychange = (e) => {
    let nam = e.target.name;
    let val = e.target.value;
    setUser({ ...user, [nam]: val });
};
  passwordMatch = () => {
     let v= user.password === user.passwordConfrim;
     if(v===false)
     {alert("password miss match. try again");
    setUser({...user,[passwordConfrim]:''})}
}


  submitRegistration = e => {
    e.preventDefault();
    
    }
    
    return (
      <div >
        <CssBaseline />

        <Paper >
          <Avatar >
            <PeopleAltIcon  />
          </Avatar>
          
            <FormControl required fullWidth margin="normal">
              
              <TextField
                autoFocus
                margin="dense"
                name="username"
                id="nameSu"
                label="Email Address"
                type="email"
                fullWidth
                onChange={MychangeSI}
                variant="standard"
                onChange={Mychange}
                required
              />
            </FormControl>

            <FormControl required fullWidth margin="normal">
              <InputLabel htmlFor="password" >
                password
              </InputLabel>
              <Input
                name="password"
                autoComplete="password"
                
                disableUnderline={true}
                onChange={this.Mychange("password")}
                type={this.state.hidePassword ? "password" : "input"}
                endAdornment={
                  this.state.hidePassword ? (
                    <InputAdornment position="end">
                      <VisibilityOffTwoToneIcon
                        fontSize="default"
                       
                        onClick={this.showPassword}
                      />
                    </InputAdornment>
                  ) : (
                    <InputAdornment position="end">
                      <VisibilityTwoToneIcon
                        fontSize="default"
                        
                        onClick={this.showPassword}
                      />
                    </InputAdornment>
                  )
                }
              />
            </FormControl>

            <FormControl required fullWidth margin="normal">
              <InputLabel htmlFor="passwordConfrim" >
                confrim password
              </InputLabel>
              <Input
                name="passwordConfrim"
                autoComplete="passwordConfrim"
                
                disableUnderline={true}
                onClick={this.state.showPassword}
                onChange={this.Mychange("passwordConfrim")}
                type={this.state.hidePassword ? "password" : "input"}
                endAdornment={
                  this.state.hidePassword ? (
                    <InputAdornment position="end">
                      <VisibilityOffTwoToneIcon
                        fontSize="default"
                       
                        onClick={this.showPassword}
                      />
                    </InputAdornment>
                  ) : (
                    <InputAdornment position="end">
                      <VisibilityTwoToneIcon
                        fontSize="default"
                        
                        onClick={this.showPassword}
                      />
                    </InputAdornment>
                  )
                }
              />
            </FormControl>
            <Button
              disabled={!this.isValid()}
              disableRipple
              fullWidth
              variant="outlined"
              
              type="submit"
              onClick={this.submitRegistration}
            >
              Join
            </Button>
          

          {this.state.error ? (
            <Snackbar
              variant="error"
              key={this.state.error}
              anchorOrigin={{
                vertical: "bottom",
                horizontal: "center"
              }}
              open={this.state.errorOpen}
              onClose={this.errorClose}
              autoHideDuration={3000}
            >
              <SnackbarContent
                
                message={
                  <div>
                    <span style={{ marginRight: "8px" }}>
                      <ErrorIcon fontSize="large" color="error" />
                    </span>
                    <span> {this.state.error} </span>
                  </div>
                }
                action={[
                  <IconButton
                    key="close"
                    aria-label="close"
                    onClick={this.errorClose}
                  >
                    <CloseIcon color="error" />
                  </IconButton>
                ]}
              />
            </Snackbar>
          ) : null}
        </Paper>
      </div>
    );
  }


export default withStyles(register)(Registration);
