import * as React from "react";
import CssBaseline from "@mui/material/CssBaseline";
import { Paper } from "@mui/material";
import { Avatar } from "@mui/material";
import { FormControl, Button} from "@mui/material"  ;
import { TextField } from "@mui/material";

const Register =()=> {
 
const [user, setUser]=React.useState({
    email: "",
    password: "",
    passwordConfrim: "",
  })
  
  const Mychange = (e) => {
    let nam = e.target.name;
    let val = e.target.value;
    setUser({ ...user, [nam]: val });
};
  const passwordMatch = () => {
     let v= user.password === user.passwordConfrim;
     if(v===false)
     {alert("password miss match. try again");
    setUser({...user,[pa]:''})}else console.log(user);
}


  const submitRegistration = e => {
    e.preventDefault();
    passwordMatch();
    }
    
    return (
      <div >
        <CssBaseline />

        <Paper >
          
            <FormControl required fullWidth margin="normal">
              
              <TextField
                autoFocus
                margin="dense"
                name="username"
                id="nameSu"
                label="Email Address"
                type="email"
                fullWidth
                variant="standard"
                onChange={Mychange}
                required
              />
            </FormControl>

            <FormControl required fullWidth margin="normal">
            <TextField
                autoFocus
                margin="dense"
                name="password"
                id="pwd"
                label="pasword"
                type="password"
                fullWidth
                variant="standard"
                onChange={Mychange}
                required
              />
            </FormControl>

            <FormControl required fullWidth margin="normal">
            <TextField
                autoFocus
                margin="dense"
                name="confirmpwd"
                id="cpwd"
                label="pasword"
                type="password"
                fullWidth
                variant="standard"
                onChange={Mychange}
                required
              />
            </FormControl>
            <Button
              fullWidth
              variant="outlined"
              
              type="submit"
              onClick={submitRegistration}
            >
              Join
            </Button>
        </Paper>
      </div>
    );
  }


export default Register;
