import React, { useState } from "react";
import {
  Box,
  Card,
  Container,
  CssBaseline,
  FormGroup,
  TextField,
} from "@mui/material";
import { Button } from "@mui/material";
import './index.css'




const Register = () => {

  // create state variables for each input
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [tel, setTel] = useState("");

  const handleClose = () => {
    setFirstName("");
    setLastName("");
    setEmail("");
    setPassword("");
    setTel("");
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(firstName, lastName, email, password); //axios post request to the backend server
    handleClose();
  };

  return (
   
    <Container >
     <div  id='InitCont'width='840px' height='800px' ></div>
      <CssBaseline>
        <Card  className='InitCont1' sx={{ margin:'auto',width:2/3}}>
        <form onSubmit={handleSubmit}>
          <Box sx={{display:'flex' ,spacing:'20',borderRadius:'50%'}}>
          <TextField
            sx={{width:1/2,mr:'3px'}}
            label="First Name"
            variant="filled"
            required
            className="textfield"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
          <TextField
            sx={{width:1/2}}
            label="Last Name"
            variant="filled"
            required
            className="textfield"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
</Box>
         
          <FormGroup>
            <TextField
            fullWidth
              label="Email"
              variant="filled"
              type="email"
              required
              className="textfield"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <TextField
            fullWidth
              label="Tel"
              variant="filled"
              type="tel"
              required
              className="textfield"
              value={tel}
              onChange={(e) => setTel(e.target.value)}
            />
            <TextField
            fullWidth
              label="Password"
              variant="filled"
              type="password"
              required
              className="textfield"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </FormGroup>
          <Box>
            <Button variant="contained" onClick={handleClose}>
              Cancel
            </Button>
            <Button type="submit" variant="contained" color="primary">
              Signup
            </Button>
          </Box>
        </form>
        </Card>
      </CssBaseline>
    </Container>
    
  );
};

export default Register;
